(()=>{"use strict";let e="";document.getElementById("weather-form").addEventListener("submit",(async function(t){t.preventDefault(),await async function(){const t=document.getElementById("city").value,n=await fetch(`https://api.weatherapi.com/v1/current.json?key=262fb141faa34d58844195452242304&q=${t}`),i=await n.json();e={city_name:i.location.name,temp_c:i.current.temp_c,temp_f:i.current.temp_f,feelslike_c:i.current.feelslike_c,feelslike_f:i.current.feelslike_f,condition:i.current.condition.text}}(),document.getElementById("weather-contents").innerHTML=`\n    <p>City: ${e.city_name}</p>\n    <p>Condition: ${e.condition}</p>\n    <p>Temp (celsius): ${e.temp_c}</p>\n    <p>Temp (farenheit): ${e.temp_f}</p>\n    <p>Feels like (celsiuis): ${e.feelslike_c}</p>\n    <p>Feels like (farenheit): ${e.feelslike_f}</p>\n  `}))})();